mapper.select:
		SELECT MEMBER_ID, MEMBER_NAME, MEMBER_EMAIL, MEMBER_ADDR
 		FROM AG_MEMBER
		WHERE MEMBER_ID = #{member_id}

mapper.update:
UPDATE AG_MEMBER
SET MEMBER_EMAIL = #{member_email}, MEMBER_ADDR = #{member_addr}
WHERE MEMBER_ID = #{member_id}

UPDATE AG_MEMBER
SET MEMBER_ENABLED = 'Y'
WHERE MEMBER_ID = #{member_id}
